useRef缓存一个值
export default function UseRefExample() {
    const [counter, setCounter] = useState(0);
    const prev = useRef(null);//引用行为
    // let prev = null;
    return (
        <div>
            <p>当前值：{counter}</p>
            <p>之前的值：{prev.current}</p>
            <Button onClick={() => {
                prev.current = counter;
                setCounter(x => {
                    console.log('x++',x)
                    return x+1;
                });
            }}>
                点我增加
            </Button>

            <Button onClick={() => {
                prev.current = counter;
                setCounter(x => {
                    console.log('x--',x);
                    return x-1
                });
            }}>
                点我减少
            </Button>

        </div>
    )

}
