import {Button} from 'antd'

export default function UseRefExample(){
    const refInput = useRef();
        return (
            <div>
                <input ref={refInput}/>
                <Button onClick={
                    ()=>{
                        refInput.current.focus();
                    }
                }>Focus</Button>
            </div>
        )

}
